INSERT INTO `dp_pages_old` (`id`,`id_parent`,`type`,`title`,`name`,`title_tag`,`content`,`alias`,`visible`,`is_main`,`module`,`keywords`,`description`) VALUES (12,0,NULL,'Ипотека в Сыктывкаре',NULL,'Ипотека в Сыктывкаре','<p><strong>Агентство</strong> недвижимости &laquo;Дом Плюс&raquo; является официальным <strong>партнер</strong>ом ОАО &laquo;<strong>Сбербанк</strong> России&raquo; по ипотечному жилищному кредитованию.</p><p><strong>Это означает, чтов ы можете не только выбрать у нас подходящую квартиру (недвижимость на первичном и вторичном рынке), но и купить ее в ипотеку при полной поддержке &nbsp;АН &laquo;Дом Плюс&raquo;</strong>.</p><p><em>Почему за ипотекой на недвижимость выгодно обращаться в &laquo;Дом Плюс&raquo;, а не напрямую в Сбербанк?</em></p><ul><li>&nbsp;Наши Клиенты &ndash; всегда в числе первых.</li></ul><p>На сайте Сбербанка у нас открыт личный кабинет, через который мы подаем заявки и отправляем документы на одобрение кредита в удобное для Вас время, без утомительных очередей и потери времени. Рассмотрение заявки, поданной через АН &laquo;Дом Плюс&raquo;, происходит <strong>в 2 раза быстрее, чем в общем порядке</strong>. Следовательно, вы узнаете о выдаче займа в числе первых.</p><p><strong>Просто позвоните нам по телефону (8212) 72-29-11</strong></p>','ipoteka',1,0,'','','');
INSERT INTO `dp_pages_old` (`id`,`id_parent`,`type`,`title`,`name`,`title_tag`,`content`,`alias`,`visible`,`is_main`,`module`,`keywords`,`description`) VALUES (13,0,NULL,'Карьера, вакансии в Дом Плюс Сыктывкар',NULL,'Карьера, вакансии в Дом Плюс Сыктывкар','<p>ОТКРЫТА ВАКАНСИЯ!!!</p><p>МЕНЕДЖЕР по НЕДВИЖИМОСТИ.</p><p>Работая на рынке недвижимости 15 лет и являясь Крупнейшей и Опытнейшей компанией, Мы разработали уникальную и эффективную систему обучения, стажировки и адаптации сотрудников.</p><p>МЫ ПРЕДЛАГАЕМ:</p><p>Обучение на базе компании ведущими профессионалами.</p><p>Кураторство ведущими специалистами.</p><p>Работу с актуальными заявками.</p><p>Обеспеченный объем работы.</p><p>Высокий процент со сделки.</p><p>Трудоустройство в соответствии с законодательством.</p><p>Гибкий подход к студентам.</p><p>Работая в партнерских взаимоотношениях наша компания, динамично развивается в перспективных направлениях, давая возможность для успешного развития энергичных и амбициозных людей.</p><p>Собеседование проводится ежедневно с 16 до 17 часов</p><p>По адресу: Сысольское ш. д.1/3</p><p>Предварительная запись после рассмотрения резюме.</p>','job',1,0,'','','');
