INSERT INTO `sod_status` (`id`,`created_at`,`updated_at`,`id_image`,`name`,`title`,`content`,`url`) VALUES (7,'2020-12-17 00:37:22','2020-12-17 00:37:22',18,'ZyXEL','ZyXEL AUTHORIZED RESALE PARTNER ','Со времен разработки первого&nbsp;модема в далеком 1990 году корпорация ZyXEL Communications специализируется на решениях для доступа в Интернет. Сегодня приоритетные направления бизнеса компании &mdash; это абонентское и&nbsp;операторское&nbsp;оборудование для широкополосного доступа по&nbsp;технологиям хDSL, Metro Ethernet, LTE и PON,&nbsp;системы сетевой безопасности и профессиональные беспроводные сети.','zyxel.ru');
INSERT INTO `sod_status` (`id`,`created_at`,`updated_at`,`id_image`,`name`,`title`,`content`,`url`) VALUES (8,'2020-12-17 00:37:22','2020-12-17 00:37:22',19,'Hiperline','Сертифицированный инсталлятор Hiperline','Hyperline является производителем полного спектра продукции для построения Структурированной Кабельной Системы (СКС).&nbsp;<br /> Комплексные решения Hyperline для больших и малых офисов, центров обработки данных и зданий включают в себя медные и волоконно-оптические кабели, коммутационные панели и модули, розетки, патч-корды, кроссы, разъемы, серверные и телекоммуникационные шкафы и стойки, монтажные инструменты, маркеры, кабельные стяжки и средства для организации кабелей.&nbsp;','www.hyperline.ru');
