INSERT INTO `sod_status` (`id`,`created_at`,`updated_at`,`id_image`,`name`,`title`,`content`,`url`) VALUES (10,'2020-12-17 00:37:22','2020-12-17 00:37:22',21,'Lenovo','Lenovo Premium Partner ','<strong>Lenovo Group Limited - </strong>Компания выпускает персональные настольные компьютеры, мобильные телефоны, нетбуки, серверы, мониторы, видеопроекторы, видеокарты, клавиатуры, мыши и комплекты, модули памяти, оптические приводы, блоки питания, аксессуары, сумки и чехлы для ноутбуков а также разрабатывает программное обеспечение для своих изделий.','lenovo.com');
INSERT INTO `sod_status` (`id`,`created_at`,`updated_at`,`id_image`,`name`,`title`,`content`,`url`) VALUES (11,'2020-12-17 00:39:15','2020-12-17 00:39:15',22,'Dr.Web','официальный партнер Dr.Web','<em>Компания &laquo;Доктор Веб&raquo;</em>&nbsp;&mdash; российская компания, являющаяся производителем и поставщиком антивирусных продуктов под маркой Dr.&nbsp;Web. Компания предлагает антивирусные решения самому широкому кругу клиентов, использующих различные операционные системы.','www.drweb.com');
INSERT INTO `sod_status` (`id`,`created_at`,`updated_at`,`id_image`,`name`,`title`,`content`,`url`) VALUES (12,'2020-12-17 00:39:15','2020-12-17 00:39:15',23,'Kaspersky','Авторизованный партнер со статусом Partner ','&laquo;Лаборатория Касперского&raquo; &ndash; одна из наиболее динамично развивающихся компаний в сфере информационной безопасности. Защитные продукты &laquo;Лаборатории Касперского&raquo; ориентированы не только на решение существующих проблем, но и на предотвращение новых, еще не известных угроз.','www.kaspersky.ru');
