INSERT INTO `sod_status` (`id`,`created_at`,`updated_at`,`id_image`,`name`,`title`,`content`,`url`) VALUES (16,'2020-12-17 00:39:17','2020-12-17 00:39:17',27,'AESP','Authorized installer','<span style=\"font-family: Tahoma; font-size: small;\"><span style=\"color: #505050;\">Компания</span><span style=\"color: #505050;\">&nbsp;<strong>AESP </strong></span><span style=\"color: #505050;\">&ndash; производитель и поставщик телекоммуникационного и сетевого оборудования под торговыми марками </span><strong><span style=\"color: #505050;\">SignaPro&trade;</span></strong><span style=\"color: #505050;\">&nbsp;</span><span style=\"color: #505050;\">и</span><span style=\"color: #505050;\">&nbsp;<strong>Signamax&trade;</strong></span><span style=\"color: #505050;\">. Работая с 1983 г. на международном рынке, </span><strong><span style=\"color: #505050;\">AESP</span></strong><span style=\"color: #505050;\">&nbsp;</span><span style=\"color: #505050;\">имеет репутацию одного из мировых лидеров в области производства высокотехнологичного оборудования, с помощью которого могут быть реализованы ИТ-проекты любой сложности. <br /></span></span>','www.aesp.ru');
INSERT INTO `sod_status` (`id`,`created_at`,`updated_at`,`id_image`,`name`,`title`,`content`,`url`) VALUES (17,'2020-12-17 00:39:17','2020-12-17 00:39:17',28,'Powercom','Авторизованный поставщик решений Powercom','Основанная в 1987 году, компания POWERCOM на сегодняшний день является всемирно известным производителем источников бесперебойного питания, оборудования для защиты электроснабжения и поставщиком решений для солнечной энергетики. Спектр разрабатываемого, производимого и поддерживаемого POWERCOM оборудования, максимально широк: от стабилизаторов напряжения и резервных ИБП для персональных компьютеров, до источников бесперебойного питания с двойным преобразованием энергии для серверного, сетевого и прочего вычислительного и телекоммуникационного оборудования.','www.pcm.ru/main');
